import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { signUpSchema } from "../../utils/validation";
import AuthInput from "./AuthInput";
import { useSelector } from "react-redux";
import { PulseLoader} from "react-spinners"
import { Link } from "react-router-dom";

function RegisterForm() {
    const {status} = useSelector((state) => state.user)
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(signUpSchema),
  });

  const onSubmit = (data) => console.log(data);


  return (
    <div className="h-screen w-full flex items-center justify-center overflow-hidden">
      {/* Container */}
      <div className="max-w-md space-y-8 p-10 dark:bg-dark_bg_2 rounded-xl">
        {/* Heading */}
        <div className="text-center dark:text-dark_text_1">
          <h2 className=" mt-6 text-3xl font-bold">Welcome</h2>
          <p className="mt-2 text-sm">Sign up</p>
        </div>
        {/* Form */}
        <form className="mt-6 space-y-6" onSubmit={handleSubmit(onSubmit)}>
          <AuthInput
            name="name"
            type="text"
            placeholder="Full Name"
            register={register}
            error={errors?.name?.message}
          />
          <AuthInput
            name="email"
            type="text"
            placeholder="Email address"
            register={register}
            error={errors?.email?.message}
          />
            <AuthInput
            name="status"
            type="text"
            placeholder="Status (optional)"
            register={register}
            error={errors?.status?.message}
          />
            <AuthInput
            name="password"
            type="password"
            placeholder="Password"
            register={register}
            error={errors?.password?.message}
          />
          {/* Submit Button */}
          <button type="submit" className="w-full flex justify-center bg-green_1 text-gray-100 p-4 rounded-full tracking-wide font-semibold focus: outline-none hover:bg-green_2 shadow-lg cursor-pointer transition ease-in duration-300">
            {status === "loading" ? <PulseLoader color="#fff" size={16} /> : "Sign up"}
          </button>
          {/* Sign in link */}
          <p className="flex flex-col items-center justify-center mt-10 text-md dark:text-dark_text_1">
            <span className="">Have an account?</span>
            <Link to="/login" className="hover:underline cursor-pointer transition ease-in duration-300 text-green-300">Sign in</Link>
          </p>
        </form>
      </div>
    </div>
  );
}

export default RegisterForm;
